<?php	//1.裁剪		function cut($name,$x1,$y1,$x2,$y2){			//1.打开图像（打开一张）		//2.创建一个空白画布		function getinfo($name){			$info = getimagesize($name);			$width = $info[0];			$height = $info[1];			$mime = $info['mime'];						switch($mime){				case 'image/png':					$res = imagecreatefrompng($name);					break;				case 'image/jpeg':					$res = imagecreatefromjpeg($name);					break;				case 'image/gif':					$res = imagecreatefromgif($name);					break;				case 'image/wbmp':					$res = imagecreatefromwbmp($name);					break;			}			return array('width'=>$width,'height'=>$height,'res'=>$res);		}		$info = getinfo($name);		$img = imagecreatetruecolor(($x2-$x1),($y2-$y1));				//3.将图片的裁剪部分截取，然后粘贴到空白画布上		imagecopymerge($img,$info['res'],0,0,$x1,$y1,($x2-$x1),($y2-$y1),100);				//4.生成随机文件名		//获取扩展名		$ext = pathinfo($name,PATHINFO_EXTENSION);		$rand_name = dirname($name)."/cut_".basename($name);				switch($ext){			case 'jpg':			case 'jpeg':			case 'jpe':				imagejpeg($img,$rand_name);				break;			case 'png':				imagepng($img,$rand_name);				break;			case 'gif':				imagegif($img,$rand_name);				break;			case 'bmp':			case 'wbmp':				imagewbmp($img,$rand_name);				break;		}				//5.销毁资源		imagedestroy($info['res']);		imagedestroy($img);	}					