<?php	function getInfo($name){		$img_info = getimagesize($name);		$img_width = $img_info[0];		$img_height = $img_info[1];		$img_mime = $img_info['mime'];				switch($img_mime){			case 'image/jpeg':				$img = imagecreatefromjpeg($name);				break;			case 'image/gif':				$img = imagecreatefromgif($name);				break;			case 'image/png':				$img = imagecreatefrompng($name);				break;			case 'image/wbmp':				$img = imagecreatefromwbmp($name);				break;		}				return array('width'=>$img_width,'height'=>$img_height,'res'=>$img);	}